//Queue.h#include <iostream>#include "Simulacion.h"#ifndef QUEUE#define QUEUEtypedef Llamada QueueElement;class Queue{ public:  Queue();  Queue(const Queue & original);  ~Queue();   const Queue & operator= (const Queue & rightHandSide);  bool empty() const;  void enqueue(const QueueElement & value);  void display(ostream & out) const;  QueueElement front() const;  void dequeue(); private:   class Node{    public:      QueueElement data;      Node * next;      Node(QueueElement value, Node * link = 0) { data = value; next = link; }  };  typedef Node * NodePointer;  NodePointer myFront, myBack;       }; #endif